import re
from Bio import Entrez

# Set your email (required by NCBI)
Entrez.email = "your_email@example.com"

# Regex pattern for DOIs
doi_pattern = r'\b10\.\d{4,9}/[-._;()/:A-Za-z0-9]+\b'

def extract_dois(text: str):
    """Extract all DOI mentions from a text string."""
    return re.findall(doi_pattern, text)

def doi_to_pmid(doi: str) -> str:
    """Convert a DOI to a PubMed PMID using NCBI Entrez."""
    try:
        handle = Entrez.esearch(db="pubmed", term=f"{doi}[DOI]")
        record = Entrez.read(handle)
        handle.close()
        idlist = record.get("IdList", [])
        return idlist[0] if idlist else None
    except Exception as e:
        print(f"Error looking up {doi}: {e}")
        return None

def process_text(text: str, output_file: str):
    """Extract DOIs, convert to PMIDs, and save to file."""
    dois = extract_dois(text)
    results = []
    
    for doi in dois:
        pmid = doi_to_pmid(doi)
        results.append((doi, pmid))
        print(f"DOI: {doi} → PMID: {pmid}")

    # Write to file
    with open(output_file, "w") as f:
        for doi, pmid in results:
            f.write(f"{doi}\t{pmid if pmid else 'NOT FOUND'}\n")

